import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.support.GroovyUtils


def groovyUtils = new GroovyUtils(context)

def holder = groovyUtils.getXmlHolder("apoCatalogProductUpdate - getProductIds#Response")

def skus = holder.getNodeValue("//parameters[1]/result[1]/skus[1]")

log.info("skus are :" + skus);

assert skus != null

// log.info("size : " + skus.size())
// log.info(skus.indexOf(","))
def simple_sku = skus.substring(0,skus.indexOf(","))

def config_sku = skus.substring(skus.indexOf(",")+1)

log.info("simple_sku : " + simple_sku)
log.info("config_sku : " + config_sku)

con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.support.GroovyUtils


def groovyUtils = new GroovyUtils(context)

def holder = groovyUtils.getXmlHolder("apoCatalogProductUpdate - getProductIds#Response")

def skus = holder.getNodeValue("//parameters[1]/result[1]/skus[1]")

log.info("skus are :" + skus);

assert skus != null

// log.info("size : " + skus.size())
// log.info(skus.indexOf(","))
def simple_sku = skus.substring(0,skus.indexOf(","))

def config_sku = skus.substring(skus.indexOf(",")+1)

log.info("simple_sku : " + simple_sku)
log.info("config_sku : " + config_sku)

assert testRunner.testCase.getPropertyValue("simple_sku") == simple_sku

// property transfer to config_sku

 testRunner.testCase.setPropertyValue("config_sku", config_sku)

// extracting the productids and identifying simple & config productIds.

def productIds = holder.getNodeValue("//parameters[1]/result[1]/ids[1]")

log.info("productIds : " + productIds)

def productId1 = productIds.substring(0,productIds.indexOf(","))
def productId2 = productIds.substring(productIds.indexOf(",")+1)

log.info("productId1  :" + productId1)
log.info("productId2  :" + productId2)

def simple_productId;
def config_productId;

if (testRunner.testCase.getPropertyValue("simple_productId") != productId1){
 simple_productId = productId2
}
else{ 
 simple_productId = productId1
}

if (testRunner.testCase.getPropertyValue("simple_productId") == productId2){
 config_productId = productId1
}
else{ 
 config_productId = productId2
}

log.info("simple_productId : " + simple_productId)
log.info("config_productId : " + config_productId)

// property transfer to config_productId

 testRunner.testCase.setPropertyValue("config_productId", config_productId)

