con:config.script:
def random_num = String.valueOf((long)(Math.random()*9999999))
def identifier = "TEST" + random_num
 //context.randomValue = String.valueOf((long)(Math.random()*99999999999))
 //log.info(context.randomValue)

 log.info("identifier :" + identifier)

  testRunner.testCase.setPropertyValue("Identifier", identifier);

  def product_group = identifier.substring(0,8)
log.info("product_group :" + product_group)

testRunner.testCase.setPropertyValue("product_group", product_group);

def style_number = identifier.substring(0,8)
log.info("style_number :" + style_number)

testRunner.testCase.setPropertyValue("style_number", style_number);

def name = ("product with  - " + style_number)

log.info("product_name : " + name)

testRunner.testCase.setPropertyValue("name", name);

def random_num = String.valueOf((long)(Math.random()*9999999))
def ean_number = 999999 + random_num
 log.info(ean_number)
 testRunner.testCase.setPropertyValue("EAN", ean_number);

// import javax.naming.Context

// def colour codes = 80006,20001,10002,11003,30004

// def initIdList = [1,2,3]
//log.info(".....initIdList = " + initIdList)

// testRunner.testCase.setPropertyValue("IdList", (String)initIdList)


def list = [1,2,3]
context.setProperty("list",list)

def list1 = context.list

//log.info("..list1 = " + list1.getAt(1))
 log.info("list size :" + list1.size())

for (int i=0; i<=list1.size()-1 ; i++)
{
	log.info(list.getAt(i));
}

