def holder = groovyUtils.getXmlHolder("apoCatalogProductUpdate - Create Single Product - Dress#Response")

log.info(holder.getNodeValue("//parameters[1]/result[1]/message[1]"))

import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.support.GroovyUtils

def groovyUtils = new GroovyUtils(context)

def holder = groovyUtils.getXmlHolder("apoCatalogProductUpdate - Create Single Product - Dress#Response")

def total = holder.getNodeValue("//parameters[1]/result[1]/total[1]")

def total_int = Integer.parseInt(total)
// def holder = new XmlHolder( messageExchange.responseContentAsXml )
// def node = holder.getDomNode( "//parameters[1]/result[1]/total[1]" )

 assert total != null

 def updated = holder.getNodeValue("//parameters[1]/result[1]/updated[1]")
 def created = holder.getNodeValue("//parameters[1]/result[1]/created[1]")

 log.info("updated = " + updated)
 log.info("created = " + created)

def updated_int = Integer.parseInt(updated)
def created_int = Integer.parseInt(created)

log.info("total of updated and created = "+ (updated_int + created_int))

assert total_int == (updated_int+created_int)

